const optionTitles = names.map((u) =>
	u
		.split('-')
		.map((w) => w.charAt(0).toUpperCase() + w.slice(1))
		.join(' ')
)

type Props = {
	illustration: string
	color: string
	height: number
	width: number
}

export class UnDraw extends React.Component<Props> {
	render() {
		const { illustration, color, height } = this.props

		return (
			<div
				style={{
					height: '100%',
					display: 'flex',
					alignItems: 'center',
					justifyContent: 'center',
					overflow: 'hidden',
					textAlign: 'center',
				}}
			>
				<Undraw
					name={illustration}
					primaryColor={color}
					height={height.toString()}
				/>
			</div>
		)
	}

	static defaultProps: Props = {
		illustration: names[0],
		color: '#1199EE',
		height: 200,
		width: 300,
	}

	static propertyControls: PropertyControls<Props> = {
		illustration: {
			type: ControlType.Enum,
			title: 'Illustration',
			options: names,
			optionTitles,
		},
		color: {
			type: ControlType.Color,
			title: 'Color',
			defaultValue: '#1199EE',
		},
	}
}
